<script setup>
import { ref } from "vue";
import PropertyCard from "@/components/PropertyCard.vue";

const search = ref("");
const type = ref("");
const price = ref("");

const items = ref([
  {
    title: "Квартира в Москве",
    price: 45000,
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTT2I-jVrkwde1iNplkDbMDRAVhWpvdI0Y9zQ&s",
    location: "москва",
    type: "Квартира"
  },
  {
    title: "Комната в центре",
    price: 30000,
    img: "data:image/jpeg;base64,",
    location: "питер",
    type: "Комната"
  }
]);

const filtered = () =>
  items.value.filter(i =>
    (!search.value || i.location.includes(search.value.toLowerCase())) &&
    (!type.value || i.type === type.value) &&
    (!price.value || i.price <= price.value)
  );
</script>

<template>
  <div class="container mt-4">
    <div class="card p-3 mb-3">
      <input v-model="search" class="form-control mb-2" placeholder="Город" />

      <select v-model="type" class="form-select mb-2">
        <option value="">Любой</option>
        <option>Квартира</option>
        <option>Дом</option>
        <option>Комната</option>
      </select>

      <select v-model="price" class="form-select">
        <option value="">Любая</option>
        <option value="30000">до 30 000</option>
        <option value="50000">до 50 000</option>
      </select>
    </div>

    <div class="row g-3">
      <PropertyCard
        v-for="item in filtered()"
        :key="item.title"
        :item="item"
      />
    </div>
  </div>
</template>
